import Layout from '../components/Layout';
import Link from 'next/link';
import fetch from 'isomorphic-unfetch';

function getPosts() {
  return [
    { id: 'hello-nextJS', title: 'hello'},
    { id: 'learn-nextJS', title: 'learn'},
    { id: 'deploy-nextJS', title: 'deploy'}
  ];
}



export default () => (
  <Layout>
    <h1>Post</h1>
    <ul>
      {getPosts().map((post) => (
        <li key={post.id}>
          <Link as={`/post/${post.id}`} href={`/post?title=${post.title}`}>
            <a>{post.title}</a>
          </Link>
        </li>
      ))}
    </ul>
    <style jsx>{`
      h1, a {
        font-famlly: "Arial",
      }

      ul {
        padding: 0;
      }

      li {
        list-style: none;
        margin: 5px 0;
      }

      a {
        text-decoration: none;
        color: blue;
      }

      a:hover {
        opacity: 0.6;
      }
    `}</style>
  </Layout>
)
